TEAMS = ["Chelsea", "LAFC"]
RESOLUTION_MAP = {
    "Yes": "p2",  # More than 3.5 goals
    "No": "p1",   # 3.5 goals or less
    "Unknown": "p3"  # Canceled or postponed
}

# Function to get match data
def get_match_data(date):
    url = f"https://api.sportsdata.io/v3/soccer/scores/json/GamesByDate/{date}"
