GAME_DATE = "2025-06-19"
PLAYER_NAME = "Tyrese Haliburton"
TEAM_ABBREVIATION = "IND"  # Indiana Pacers
OPPONENT_ABBREVIATION = "OKC"  # Oklahoma City Thunder
RESOLUTION_MAP = {
    "Yes": "p2",
    "No": "p1",
    "Unknown": "p3"
}

