    current_date = datetime.now()
    event_date = datetime(2025, 7, 28, 23, 59)  # Event date July 28, 2025 11:59 PM ET
    if current_date > event_date:
        url = f"{PRIMARY_ENDPOINT}/scores/json/TeamSeasonStats/2025"
