    for i in range(retries):
        try:
            response = requests.get(url, timeout=10)
            if response.ok:
                return response.json()
            if response.status_code == 429:
                time.sleep(backoff * (2 ** i))
        except requests.RequestException as e:
            print(f"Request failed: {e}")
    return None

# ─────────── helpers ───────────
