HEADERS = {"Ocp-Apim-Subscription-Key": API_KEY}
PRIMARY_ENDPOINT = "https://api.sportsdata.io/v3/mlb/scores/json"
PROXY_ENDPOINT = "https://minimal-ubuntu-production.up.railway.app/sportsdata-io-proxy/mlb"

# Game details
GAME_DATE = "2025-06-18"
PHILLIES_KEY = "PHI"
MARLINS_KEY = "MIA"

# Resolution map
RESOLUTION_MAP = {
    "Phillies": "p2",
    "Marlins": "p1",
    "50-50": "p3",
    "Too early to resolve": "p4"
}

def get_data(url):
    try:
        response = requests.get(url, headers=HEADERS, timeout=10)
        response.raise_for_status()
        return response.json()
    except requests.exceptions.RequestException as e:
        print(f"Error fetching data: {e}")
        return None

def find_game(games, home_team, away_team):
    for game in games:
        if game['HomeTeam'] == home_team and game['AwayTeam'] == away_team:
            return game
    return None

def resolve_market(game):
    if not game:
        return RESOLUTION_MAP["Too early to resolve"]
    if game['Status'] == "Final":
        if game['HomeTeamRuns'] > game['AwayTeamRuns']:
            return RESOLUTION_MAP["Phillies"] if game['HomeTeam'] == PHILLIES_KEY else RESOLUTION_MAP["Marlins"]
        else:
            return RESOLUTION_MAP["Marlins"] if game['HomeTeam'] == MARLINS_KEY else RESOLUTION_MAP["Phillies"]
    elif game['Status'] == "Canceled":
        return RESOLUTION_MAP["50-50"]
    elif game['Status'] == "Postponed":
        return RESOLUTION_MAP["Too early to resolve"]
    else:
        return RESOLUTION_MAP["Too early to resolve"]

def main():
    # Try proxy endpoint first
    games = get_data(f"{PROXY_ENDPOINT}/GamesByDate/{GAME_DATE}")
    if not games:
        # Fallback to primary endpoint
        games = get_data(f"{PRIMARY_ENDPOINT}/GamesByDate/{GAME_DATE}")

    if games:
        game = find_game(games, PHILLIES_KEY, MARLINS_KEY)
        result = resolve_market(game)
    else:
        result = RESOLUTION_MAP["Too early to resolve"]

    print(f"recommendation: {result}")

if __name__ == "__main__":
    main()

