# Game details
GAME_DATE = "2025-06-10"
HOME_TEAM = "Brewers"
AWAY_TEAM = "Braves"

# Resolution map
RESOLUTION_MAP = {
    HOME_TEAM: "p1",
    AWAY_TEAM: "p2",
    "Postponed": "p4",
    "Canceled": "p3",
    "Final": "final"
}

