    url = f"{PRIMARY_ENDPOINT}/scores/json/GamesByDate/{formatted_date}"

    # Try proxy first
    games = make_request(PROXY_ENDPOINT + url, HEADERS)
    if games is None:
        # Fallback to primary endpoint
        games = make_request(url, HEADERS)

