TEAM1 = "Los Angeles Dodgers"  # Corresponds to p1
TEAM2 = "San Francisco Giants"  # Corresponds to p2
RESOLUTION_MAP = {
    TEAM1: "p1",
    TEAM2: "p2",
    "50-50": "p3",
    "Too early to resolve": "p4"
}

