# Constants
DATE = "2025-05-30"
TEAM1 = "Washington Nationals"
TEAM2 = "Arizona Diamondbacks"
RESOLUTION_MAP = {
    TEAM1: "p2",  # Nationals win
    TEAM2: "p1",  # Diamondbacks win
    "Postponed": "p4",  # Game postponed
    "Canceled": "p3",  # Game canceled
    "Unknown": "p4"  # Unknown or in-progress
}

